package Day_2;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		for(int i = 0;i<n;i++) {
			System.out.println(i);
		}
		int a[]= {10,20,30,40,50,60,70};
		for(int x:a)
		{
			System.out.println(x);
//			System.out.println(type(x))
		}
	}

}
